mapper.select:
SELECT
IFNULL(SUM(t.merchant_performance),0.00) AS sum_performance,
IFNULL(SUM(t.merchant_num),0.00) AS sum_num
FROM
t_summary_traditional_pos_trans_all t
INNER JOIN t_user_info t1 ON t.user_id = t1.id
INNER JOIN t_user_traditional_pos_info t2 on t.sn=t2.sn and t1.id=t2.user_id

	(t1.id=#{map.uid} or t1.parent_chain like concat(#{map.chain},'%'))
	AND t.pos_type='epos'
	AND t2.pos_type='epos'

		AND t.id = #{map.id}


		AND t.sn = #{map.sn}


		AND t1.user_tel = #{map.user_tel}


		AND t2.state_status = #{map.state_status}


		AND t2.trade_status = #{map.trade_status}



		AND t.up_date >= date_format(#{map.beginTime},'%Y%m%d')



		AND t.up_date <= date_format(#{map.endTime},'%Y%m%d')



${map.dataScope}

SELECT t.id, t.sn, t.user_id, t.performance, t.num, t.merchant_performance, t.merchant_num, t.agency_performance, t.agency_num, t.benefit, t.share_benefit, t.single_benefit, t.return_benefit, t.activity_benefit, t.merchant_benefit, t.merchant_share_benefit, t.merchant_single_benefit, t.merchant_return_benefit, t.merchant_activity_benefit, t.agency_benefit, t.agency_share_benefit, t.agency_single_benefit, t.agency_return_benefit, DATE_FORMAT( STR_TO_DATE( CONCAT(t.up_date, t.up_time), '%Y%m%d%H%i%s' ), '%Y-%m-%d %H:%i:%s' ) AS up_date, t1.manager_id, t1.user_tel, t1.real_name, t2.state_status, t2.trade_status FROM t_summary_traditional_pos_trans_all t INNER JOIN t_user_info t1 ON t.user_id = t1.id INNER JOIN t_user_traditional_pos_info t2 on t.sn=t2.sn and t1.id=t2.user_id
(t1.id=#{map.uid} or t1.parent_chain like concat(#{map.chain},'%')) AND t.pos_type='epos' AND t2.pos_type='epos'
AND t.id = #{map.id}
AND t.sn = #{map.sn}
AND t1.real_name = #{map.real_name}
AND t1.user_tel = #{map.user_tel}
AND t2.state_status = #{map.state_status}
AND t2.trade_status = #{map.trade_status}
AND t.up_date >= date_format(#{map.beginTime},'%Y%m%d')
AND t.up_date <= date_format(#{map.endTime},'%Y%m%d')
${map.dataScope}

