mapper.select:
SELECT t.id, t.activity_id, t.pos_num, t.reward_money, t.expenditure, t.del, t.remark, t.create_by, t.update_by, DATE_FORMAT( STR_TO_DATE( CONCAT(t.cre_date, t.cre_time), '%Y%m%d%H%i%s' ), '%Y-%m-%d %H:%i:%s' ) AS cre_date, DATE_FORMAT( STR_TO_DATE( CONCAT(t.up_date, t.up_time), '%Y%m%d%H%i%s' ), '%Y-%m-%d %H:%i:%s' ) AS up_date, t1.manager_id, t1.order_id, t1.activity_name, t1.activity_type, t1.start_date, t1.end_date, t1.status, t1.del AS activity_del FROM t_sys_mpos_activity_reward_info t LEFT JOIN t_sys_mpos_activity_info t1 ON t.activity_id = t1.id
t1.manager_id = #{map.manager_id}
AND t.id = #{id}
AND t.activity_id = #{map.activity_id}
AND t.del = #{map.del}
AND t1.order_id = #{map.order_id}
AND t1.activity_type = #{map.activity_type}
AND t.cre_date >= date_format(#{map.beginTime},'%Y%m%d')
AND t.cre_date <= date_format(#{map.endTime},'%Y%m%d')
${map.dataScope}

SELECT t.id, t.activity_id, t.pos_num, t.reward_money, t.expenditure, t.del, t.remark, t.create_by, t.update_by, DATE_FORMAT( STR_TO_DATE( CONCAT(t.cre_date, t.cre_time), '%Y%m%d%H%i%s' ), '%Y-%m-%d %H:%i:%s' ) AS cre_date, DATE_FORMAT( STR_TO_DATE( CONCAT(t.up_date, t.up_time), '%Y%m%d%H%i%s' ), '%Y-%m-%d %H:%i:%s' ) AS up_date, t1.manager_id, t1.order_id, t1.activity_name, t1.activity_type, t1.start_date, t1.end_date FROM t_sys_mpos_activity_reward_info t LEFT JOIN t_sys_mpos_activity_info t1 ON t.activity_id = t1.id
t1.manager_id = #{map.manager_id}
AND t.id = #{id}
AND t.activity_id = #{map.activity_id}
AND t.del = #{map.del}
AND t1.order_id = #{map.order_id}
AND t1.activity_type = #{map.activity_type}
AND t.cre_date >= date_format(#{map.beginTime},'%Y%m%d')
AND t.cre_date <= date_format(#{map.endTime},'%Y%m%d')
${map.dataScope}

mapper.update:
update 
	t_sys_mpos_activity_reward_info t
	LEFT JOIN t_sys_mpos_activity_info t1 ON t.activity_id=t1.id
SET t.pos_num=#{map.pos_num},t.reward_money=#{map.reward_money},t.expenditure=#{map.expenditure},
	t.up_date=#{map.up_date},t.up_time=#{map.up_time},t.remark=#{map.remark},t.update_by=#{map.update_by} 
where 
	t.id=#{map.reward_id} and t.del=#{map.old_del} 
	and t1.status=#{map.old_status} and t1.del=#{map.old_activity_del} 
	and t1.manager_id=#{map.maneger_id}

update 
	t_sys_mpos_activity_reward_info t
	LEFT JOIN t_sys_mpos_activity_info t1 ON t.activity_id=t1.id
SET t.del=#{map.new_del},
	t.up_date=#{map.up_date},t.up_time=#{map.up_time},t.remark=#{map.remark},t.update_by=#{map.update_by} 
where 
	t.id=#{map.reward_id} and t.del=#{map.old_del} 
	and t1.status=#{map.old_status} 
	and t1.manager_id=#{map.manager_id}
