mapper.select:
SELECT t.id, t.user_id, t.sn, t.credit_card_rate_old, t.credit_card_rate_new, t.card_settle_price, t.status, t.remark, t.update_by, DATE_FORMAT( STR_TO_DATE( CONCAT(t.cre_date, t.cre_time), '%Y%m%d%H%i%s' ), '%Y-%m-%d %H:%i:%s' ) AS cre_date, DATE_FORMAT( STR_TO_DATE( CONCAT(t.up_date, t.up_time), '%Y%m%d%H%i%s' ), '%Y-%m-%d %H:%i:%s' ) AS up_date, t1.manager_id, t1.user_tel, t1.real_name FROM t_user_apply_cardrate_mpos_record_info t LEFT JOIN t_user_info t1 ON t.user_id = t1.id
t1.manager_id = #{map.manager_id}
AND t.id = #{map.id}
AND t.status = #{map.status}
AND t.sn = #{map.sn}
AND t1.user_tel = #{map.user_tel}
AND t1.real_name = #{map.real_name}
AND t.cre_date >= date_format(#{map.beginTime},'%Y%m%d')
AND t.cre_date <= date_format(#{map.endTime},'%Y%m%d')
${map.dataScope}

SELECT t.id, t.user_id, t.sn, t.credit_card_rate_old, t.credit_card_rate_new, t.card_settle_price, t.status, t.remark, t.update_by, DATE_FORMAT( STR_TO_DATE( CONCAT(t.cre_date, t.cre_time), '%Y%m%d%H%i%s' ), '%Y-%m-%d %H:%i:%s' ) AS cre_date, DATE_FORMAT( STR_TO_DATE( CONCAT(t.up_date, t.up_time), '%Y%m%d%H%i%s' ), '%Y-%m-%d %H:%i:%s' ) AS up_date, t1.manager_id, t1.user_tel, t1.real_name FROM t_user_apply_cardrate_mpos_record_info t LEFT JOIN t_user_info t1 ON t.user_id = t1.id
t1.manager_id = #{map.manager_id}
AND t.id = #{map.id}
AND t.status = #{map.status}
AND t.sn = #{map.sn}
AND t1.user_tel = #{map.user_tel}
AND t1.real_name = #{map.real_name}
AND t.cre_date >= date_format(#{map.beginTime},'%Y%m%d')
AND t.cre_date <= date_format(#{map.endTime},'%Y%m%d')
${map.dataScope}

mapper.update:
update t_user_apply_cardrate_mpos_record_info 
	set status=#{map.new_status},remark=#{map.remark},
		up_date=#{map.up_date},up_time=#{map.up_time},update_by=#{map.update_by}
where id=#{map.apply_id} and status=#{map.old_status}
