		AND ttt.real_name = #{map.real_name}


		AND t.cash_back_status = #{map.cash_back_status}


		AND t.act_status = #{map.act_status}



		AND t.act_date >= date_format(#{map.beginTime},'%Y%m%d')



		AND t.act_date <= date_format(#{map.endTime},'%Y%m%d')



${map.dataScope}
