mapper.select:
SELECT
	t.id,
	t.order_id,
	t.assess_id,
	t.user_id,
	t.sn,
	t.money,
	DATE_FORMAT(
		STR_TO_DATE(
			CONCAT(t.cre_date, t.cre_time),
			'%Y%m%d%H%i%s'
		),
		'%Y-%m-%d %H:%i:%s'
	) AS up_date,
	t1.manager_id,
	t1.user_tel,
	t1.real_name,
	(select policy_name from t_sys_pos_policy_define where id=t.policy_id) as policy_name
FROM
	t_user_mpos_deduct_record t
LEFT JOIN t_user_info t1 ON t.user_id = t1.id

	(t1.id=#{map.uid} or t1.parent_chain like concat(#{map.chain},'%'))

		AND t.id = #{map.id}


		AND t.order_id = #{map.order_id}


		AND t.user_id = #{map.user_id}


		AND t.sn = #{map.sn}


		AND t.assess_id = #{map.assess_id}


		AND t1.user_tel = #{map.send_user_tel}


		AND t1.real_name = #{map.real_name}




		AND t.cre_date >= date_format(#{map.beginTime},'%Y%m%d')



		AND t.cre_date <= date_format(#{map.endTime},'%Y%m%d')



${map.dataScope}

