mapper.select:
SELECT t.id, t.user_id, t.manager_id, t.assess_name, t.assess_type, t.status, t.sn_list, t.deduct_money, t.expenditure, t.start_date, t.end_date, CASE WHEN #{map.today_date} < t.start_date THEN "0" WHEN #{map.today_date} >= t.start_date AND #{map.today_date} <= t.end_date THEN "1" WHEN #{map.today_date} > t.end_date THEN "2" END AS open_status, t.remark, t.create_by, t.update_by, DATE_FORMAT( STR_TO_DATE( CONCAT(t.cre_date, t.cre_time), '%Y%m%d%H%i%s' ), '%Y-%m-%d %H:%i:%s' ) AS cre_date, DATE_FORMAT( STR_TO_DATE( CONCAT(t.up_date, t.up_time), '%Y%m%d%H%i%s' ), '%Y-%m-%d %H:%i:%s' ) AS up_date, t1.user_tel, t1.real_name FROM t_user_trapos_assess_info t LEFT JOIN t_user_info t1 ON t.user_id = t1.id
t.manager_id = #{map.manager_id}
AND t.id = #{map.id}
AND t.assess_type = #{map.assess_type}
AND t.status = #{map.status}
AND t1.user_tel = #{map.user_tel}
AND t1.real_name = #{map.real_name}
AND t.start_date >= date_format(#{map.map.activityBeginTime},'%Y%m%d')
AND t.end_date <= date_format(#{map.activityEndTime},'%Y%m%d')
AND t.cre_date >= date_format(#{map.beginTime},'%Y%m%d')
AND t.cre_date <= date_format(#{map.endTime},'%Y%m%d')
${map.dataScope}

