mapper.select:
SELECT
	t.id,
	t.send_user_id,
	t.acce_user_id,
	t.sn,
	t.status,
	DATE_FORMAT(
		STR_TO_DATE(
			CONCAT(t.cre_date, t.cre_time),
			'%Y%m%d%H%i%s'
		),
		'%Y-%m-%d %H:%i:%s'
	) AS cre_date,
	DATE_FORMAT(
		STR_TO_DATE(
			CONCAT(t.up_date, t.up_time),
			'%Y%m%d%H%i%s'
		),
		'%Y-%m-%d %H:%i:%s'
	) AS up_date,
	t1.manager_id,
	t2.user_tel AS send_user_tel,
	t2.real_name AS send_real_name,
	t3.user_tel AS acce_user_tel,
	t3.real_name AS acce_real_name
FROM
	t_user_trapos_recall_record_info t
INNER JOIN t_sys_traditional_pos_info t1 ON t.sn = t1.sn
INNER JOIN t_user_info t2 ON t.send_user_id = t2.id
INNER JOIN t_user_info t3 ON t.acce_user_id = t3.id

	(t2.id=#{map.uid} or t2.parent_chain like concat(#{map.chain},'%'))

		AND t.id = #{map.id}


		AND t.send_user_id = #{map.send_user_id}


		AND t2.real_name = #{map.send_real_name}


		AND t.acce_user_id = #{map.acce_user_id}


		AND t3.real_name = #{map.acce_real_name}


		AND t.sn = #{map.sn}


		AND t.status = #{map.status}


		AND t2.user_tel = #{map.send_user_tel}


		AND t3.user_tel = #{map.acce_user_tel}



		AND t.cre_date >= date_format(#{map.beginTime},'%Y%m%d')



		AND t.cre_date <= date_format(#{map.endTime},'%Y%m%d')



${map.dataScope}

SELECT t.id, t.send_user_id, t.acce_user_id, t.sn, t.status, DATE_FORMAT( STR_TO_DATE( CONCAT(t.cre_date, t.cre_time), '%Y%m%d%H%i%s' ), '%Y-%m-%d %H:%i:%s' ) AS cre_date, DATE_FORMAT( STR_TO_DATE( CONCAT(t.up_date, t.up_time), '%Y%m%d%H%i%s' ), '%Y-%m-%d %H:%i:%s' ) AS up_date, t1.manager_id, t2.user_tel AS send_user_tel, t2.real_name AS send_real_name, t3.user_tel AS acce_user_tel, t3.real_name AS acce_real_name FROM t_user_trapos_recall_record_info t INNER JOIN t_sys_traditional_pos_info t1 ON t.sn = t1.sn INNER JOIN t_user_info t2 ON t.send_user_id = t2.id INNER JOIN t_user_info t3 ON t.acce_user_id = t3.id
(t2.id=#{map.uid} or t2.parent_chain like concat(#{map.chain},'%'))
AND t.id = #{map.id}
AND t.send_user_id = #{map.send_user_id}
AND t.acce_user_id = #{map.acce_user_id}
AND t.sn = #{map.sn}
AND t.status = #{map.status}
AND t2.user_tel = #{map.send_user_tel}
AND t3.user_tel = #{map.acce_user_tel}
AND t.cre_date >= date_format(#{map.beginTime},'%Y%m%d')
AND t.cre_date <= date_format(#{map.endTime},'%Y%m%d')
${map.dataScope}
