mapper.select:
SELECT id, type, rate, remark, create_by, update_by, DATE_FORMAT( STR_TO_DATE( CONCAT(cre_date, cre_time), '%Y%m%d%H%i%s' ), '%Y-%m-%d %H:%i:%s' ) AS cre_date, DATE_FORMAT( STR_TO_DATE( CONCAT(up_date, up_time), '%Y%m%d%H%i%s' ), '%Y-%m-%d %H:%i:%s' ) AS up_date FROM t_sys_param_rate
type = #{map.type}
AND cre_date >= date_format(#{map.beginTime},'%Y%m%d')
AND cre_date <= date_format(#{map.endTime},'%Y%m%d')
${map.dataScope}

SELECT id, type, rate, remark, create_by, update_by, DATE_FORMAT( STR_TO_DATE( CONCAT(cre_date, cre_time), '%Y%m%d%H%i%s' ), '%Y-%m-%d %H:%i:%s' ) AS cre_date, DATE_FORMAT( STR_TO_DATE( CONCAT(up_date, up_time), '%Y%m%d%H%i%s' ), '%Y-%m-%d %H:%i:%s' ) AS up_date FROM t_sys_param_rate
type = #{map.type}
AND cre_date >= date_format(#{map.beginTime},'%Y%m%d')
AND cre_date <= date_format(#{map.endTime},'%Y%m%d')
${map.dataScope}

mapper.update:
update t_sys_param_rate
rate = #{map.rate}, remark = #{map.remark}, update_by = #{map.update_by}, up_date = #{map.up_date}, up_time = #{map.up_time}
where id = #{map.param_rate_id}
