mapper.select:
select t.id,tt.manager_id,tt.real_name,t.sn,t.card_settle_price,t.cloud_settle_price,t.single_profit_rate,t.cash_back_rate,
t.state_status,t.del,t.trade_status,t.cre_date,ttt.credit_card_rate,ttt.cloud_flash_rate,ttt.act_status,
ttt.dis_status,ttt.act_date,ttt.cash_back_type,ttt.cash_back_status,ttt.dis_status
from t_user_traditional_pos_info t inner join t_user_info tt on t.user_id=tt.id inner join t_sys_traditional_pos_info ttt on t.sn=ttt.sn

	tt.manager_id = #{map.manager_id}
	and t.user_id=#{map.user_id}
	and t.pos_type='epos'

		AND t.id = #{map.id}


		AND t.sn = #{map.sn}


		AND t.state_status = #{map.state_status}


		AND ttt.act_status = #{map.act_status}


		AND ttt.cash_back_status = #{map.cash_back_status}


		AND ttt.cash_back_type = #{map.cash_back_type}


		AND ttt.dis_status = #{map.dis_status}


		AND t.trade_status = #{map.trade_status}



		AND t.cre_date >= date_format(#{map.beginTime},'%Y%m%d')



		AND t.cre_date <= date_format(#{map.endTime},'%Y%m%d')



${map.dataScope}

SELECT t.id, t.manager_id, t.sn, t.mer_id, t.mer_name, t.name, t.tel, t.cash_back_type, t.cash_back_condition, t.cash_back_money, t.real_cash_back_money, t.cash_back_status, t.credit_card_rate, t.cloud_flash_rate, t.weixin_rate, t.zhifubao_rate, t.credit_card_rate_vip, t.cloud_flash_rate_vip, t.weixin_rate_vip, t.zhifubao_rate_vip, t.scan_status, t.act_status, t.dis_status, t.act_user_id, t.remark, t.create_by, t.update_by, t.account_id, DATE_FORMAT( STR_TO_DATE( CONCAT(t.act_date, t.act_time), '%Y%m%d%H%i%s' ), '%Y-%m-%d %H:%i:%s' ) AS act_date, DATE_FORMAT( STR_TO_DATE( CONCAT(t.cre_date, t.cre_time), '%Y%m%d%H%i%s' ), '%Y-%m-%d %H:%i:%s' ) AS cre_date, DATE_FORMAT( STR_TO_DATE( CONCAT(t.up_date, t.up_time), '%Y%m%d%H%i%s' ), '%Y-%m-%d %H:%i:%s' ) AS up_date, t1.app_id FROM t_sys_traditional_pos_info t LEFT JOIN sys_user_account t1 ON t.account_id = t1.id
t.manager_id = #{map.manager_id} AND t.pos_type = 'epos'
AND t.id = #{map.id}
AND t.sn = #{map.sn}
AND t.mer_id = #{map.mer_id}
AND t.mer_name = #{map.mer_name}
AND t.name = #{map.name}
AND t.tel = #{map.tel}
AND t.cash_back_type = #{map.cash_back_type}
