mapper.select:
SELECT t.id, t.order_id, t.user_id, t.org_no, t.org_name, t.account_type, t.cash_money, t.cash_actual_money, t.feet_rate, t.rate_feet_money, t.single_feet_money, t.deduct_money, t.account, t.account_name, t.id_card, t.bank_code, t.bank_name, t.batch_no, t.status, t.remark, t.update_by, DATE_FORMAT( STR_TO_DATE( CONCAT(t.cre_date, t.cre_time), '%Y%m%d%H%i%s' ), '%Y-%m-%d %H:%i:%s' ) AS cre_date, DATE_FORMAT( STR_TO_DATE( CONCAT(t.up_date, t.up_time), '%Y%m%d%H%i%s' ), '%Y-%m-%d %H:%i:%s' ) AS up_date, t1.manager_id, t1.user_tel, t1.real_name FROM t_user_cash_record t LEFT JOIN t_user_info t1 ON t.user_id = t1.id
t1.manager_id = #{map.manager_id}
AND t.id = #{map.id}
AND t.order_id = #{map.order_id}
AND t.user_id = #{map.user_id}
AND t.account_type = #{map.account_type}
AND t.account = #{map.account}
AND t.account_name like concat('%', #{map.account_name}, '%')
AND t.bank_code = #{map.bank_code}
AND t.bank_name like concat('%', #{map.bank_name}, '%')
AND t.batch_no = #{map.batch_no}
AND t.status = #{map.status}
AND t1.user_tel = #{map.user_tel}
AND t1.real_name = #{map.real_name}
AND t.cre_date >= date_format(#{map.beginTime},'%Y%m%d')
AND t.cre_date <= date_format(#{map.endTime},'%Y%m%d')
${map.dataScope}

SELECT
	IFNULL(sum(t.cash_money),0.00) AS sum_cash_money,
	IFNULL(sum(t.cash_actual_money),0.00) AS sum_cash_actual_money,
	IFNULL(sum(t.rate_feet_money),0.00) AS sum_rate_feet_money,
	IFNULL(sum(t.single_feet_money),0.00) AS sum_single_feet_money,
	IFNULL(sum(t.deduct_money),0.00) AS sum_deduct_money
FROM
	t_user_cash_record t
LEFT JOIN t_user_info t1 ON t.user_id = t1.id

	t1.manager_id = #{map.manager_id}

		AND t.id = #{map.id}


		AND t.order_id = #{map.order_id}


		AND t.user_id = #{map.user_id}


		AND t.account_type = #{map.account_type}


		AND t.account = #{map.account}


		AND t.account_name like concat('%', #{map.account_name}, '%')


		AND t.bank_code = #{map.bank_code}


		AND t.bank_name like concat('%', #{map.bank_name}, '%')


		AND t.batch_no = #{map.batch_no}


		AND t.status = #{map.status}


		AND t1.user_tel = #{map.user_tel}


		AND t1.real_name = #{map.real_name}



		AND t.cre_date >= date_format(#{map.beginTime},'%Y%m%d')



		AND t.cre_date <= date_format(#{map.endTime},'%Y%m%d')



${map.dataScope}

SELECT t.id, t.order_id, t.user_id, t.org_no, t.org_name, t.account_type, t.cash_money, t.cash_actual_money, t.feet_rate, t.rate_feet_money, t.single_feet_money, t.deduct_money, t.account, t.account_name, t.id_card, t.bank_code, t.bank_name, t.batch_no, t.status, t.remark, t.update_by, DATE_FORMAT( STR_TO_DATE( CONCAT(t.cre_date, t.cre_time), '%Y%m%d%H%i%s' ), '%Y-%m-%d %H:%i:%s' ) AS cre_date, DATE_FORMAT( STR_TO_DATE( CONCAT(t.up_date, t.up_time), '%Y%m%d%H%i%s' ), '%Y-%m-%d %H:%i:%s' ) AS up_date, t1.manager_id, t1.user_tel, t1.real_name FROM t_user_cash_record t LEFT JOIN t_user_info t1 ON t.user_id = t1.id
t1.manager_id = #{map.manager_id}
AND t.id = #{map.id}
AND t.order_id = #{map.order_id}
AND t.user_id = #{map.user_id}
AND t.account_type = #{map.account_type}
AND t.account = #{map.account}
AND t.account_name like concat('%', #{map.account_name}, '%')
AND t.bank_code = #{map.bank_code}
AND t.bank_name like concat('%', #{map.bank_name}, '%')
AND t.batch_no = #{map.batch_no}
AND t.status = #{map.status}
AND t1.user_tel = #{map.user_tel}
AND t1.real_name = #{map.real_name}
AND t.cre_date >= date_format(#{map.beginTime},'%Y%m%d')
AND t.cre_date <= date_format(#{map.endTime},'%Y%m%d')
${map.dataScope}

SELECT id, cash_id, cash_status, note, remark, create_by, DATE_FORMAT( STR_TO_DATE( CONCAT(cre_date, cre_time), '%Y%m%d%H%i%s' ), '%Y-%m-%d %H:%i:%s' ) AS cre_date FROM t_user_cash_record_detail
id = #{map.id}
AND cash_id = #{map.cash_id}
AND cash_status = #{map.cash_status}
AND cre_date >= date_format(#{map.beginTime},'%Y%m%d')
AND cre_date <= date_format(#{map.endTime},'%Y%m%d')
${map.dataScope}

SELECT t.id, t2.sub_agent_account, t1.manager_id as org_no, t1.manager_id as org_name, t.account_name, t.account, "11" AS account_type, t.bank_code, t.bank_name, t.cash_actual_money, t.id_card, "" AS remark, #{map.batch_no} AS batch_no, t.order_id FROM t_user_cash_record t LEFT JOIN t_user_info t1 ON t.user_id = t1.id LEFT JOIN sys_user t2 ON t1.manager_id=t2.user_id
t1.manager_id = #{map.manager_id} and t.status='00'
AND t.id = #{map.id}
AND t.order_id = #{map.order_id}
AND t.user_id = #{map.user_id}
AND t.account_type = #{map.account_type}
AND t.account = #{map.account}
AND t.account_name like concat('%', #{map.account_name}, '%')
AND t.bank_code = #{map.bank_code}
AND t.bank_name like concat('%', #{map.bank_name}, '%')
AND t.status = #{map.status}
AND t1.user_tel = #{map.user_tel}
AND t1.real_name = #{map.real_name}
AND t.cre_date >= date_format(#{map.beginTime},'%Y%m%d')
AND t.cre_date <= date_format(#{map.endTime},'%Y%m%d')
${map.dataScope}

mapper.update:
update t_user_cash_record set 

		batch_no=#{map.batch_no},


		remark=#{map.remark},

	status=#{map.new_status},up_date=#{map.cre_date},
	up_time=#{map.cre_time},update_by=#{map.create_by}
where id=#{map.cash_id} and status=#{map.old_status}
