mapper.select:
SELECT t.id, t.manager_id, t.user_tel, t.id_card, t.card_photo, t.real_name, t.money, t.settle_money, t.deduct_money, t.today_benefit, t.total_benefit, t.settle_single_feet_money, t.single_rate_feet_money, t.head_photo, t.referer_id, t.status, t.auth_status, t.report_status, t.referer_num, t.under_num, t.algebra, t.parent_chain, t.device_type, t.device_no, t.version_no, DATE_FORMAT( STR_TO_DATE( CONCAT(t.cre_date, t.cre_time), '%Y%m%d%H%i%s' ), '%Y-%m-%d %H:%i:%s' ) AS cre_date, DATE_FORMAT( STR_TO_DATE( CONCAT(t.up_date, t.up_time), '%Y%m%d%H%i%s' ), '%Y-%m-%d %H:%i:%s' ) AS up_date, DATE_FORMAT( STR_TO_DATE( CONCAT( t.last_login_date, t.last_login_time ), '%Y%m%d%H%i%s' ), '%Y-%m-%d %H:%i:%s' ) AS last_login_date, t.last_login_ip, t1.user_tel AS referer_user_tel, t1.real_name AS referer_real_name, t1.head_photo AS referer_head_photo FROM t_user_info t LEFT JOIN t_user_info t1 ON t.referer_id = t1.id
t.manager_id = #{map.manager_id}
AND t.id = #{map.id}
AND t.user_tel = #{map.user_tel}
AND t.id_card = #{map.id_card}
AND t.real_name like concat('%', #{map.real_name}, '%')
AND t.status = #{map.status}
AND t.auth_status = #{map.auth_status}
AND t.report_status = #{map.report_status}
AND t.device_type = #{map.device_type}
AND t.device_no = #{map.device_no}
AND t.version_no = #{map.version_no}
AND t.algebra = #{map.algebra}
AND t1.user_tel = #{map.referer_user_tel}
AND t1.real_name like concat('%', #{map.referer_real_name}, '%')
AND t.cre_date >= date_format(#{map.beginTime},'%Y%m%d')
AND t.cre_date <= date_format(#{map.endTime},'%Y%m%d')
${map.dataScope}

SELECT
	IFNULL(sum(t.money),0.00) AS sum_money,
	IFNULL(sum(t.settle_money),0.00) AS sum_settle_money,
	IFNULL(sum(t.deduct_money),0.00) AS sum_deduct_money,
	IFNULL(sum(t.today_benefit),0.00) AS sum_today_benefit,
	IFNULL(sum(t.total_benefit),0.00) AS sum_total_benefit,
	IFNULL(sum(t.settle_single_feet_money),0.00) AS sum_settle_single_feet_money,
	IFNULL(sum(t.single_rate_feet_money),0.00) AS sum_single_rate_feet_money
FROM
	t_user_info t
LEFT JOIN t_user_info t1 ON t.referer_id = t1.id

	t.manager_id = #{map.manager_id}

		AND t.id = #{map.id}


		AND t.user_tel = #{map.user_tel}


		AND t.id_card = #{map.id_card}


		AND t.real_name like concat('%', #{map.real_name}, '%')


		AND t.status = #{map.status}


		AND t.auth_status = #{map.auth_status}


		AND t.report_status = #{map.report_status}


		AND t.device_type = #{map.device_type}


		AND t.device_no = #{map.device_no}


		AND t.version_no = #{map.version_no}


		AND t1.user_tel = #{map.referer_user_tel}


		AND t1.real_name like concat('%', #{map.referer_real_name}, '%')



		AND t.cre_date >= date_format(#{map.beginTime},'%Y%m%d')



		AND t.cre_date <= date_format(#{map.endTime},'%Y%m%d')



${map.dataScope}

