mapper.select:
SELECT
	t.id,
	t.user_id,
	t.sn,
	t.status,
	t.remark,
	t.update_by,
	DATE_FORMAT(
		STR_TO_DATE(
			CONCAT(t.cre_date, t.cre_time),
			'%Y%m%d%H%i%s'
		),
		'%Y-%m-%d %H:%i:%s'
	) AS cre_date,
	DATE_FORMAT(
		STR_TO_DATE(
			CONCAT(t.up_date, t.up_time),
			'%Y%m%d%H%i%s'
		),
		'%Y-%m-%d %H:%i:%s'
	) AS up_date,
	t1.manager_id,
	t2.user_tel,
	t2.real_name 
FROM
	t_user_mpos_unbind_record_info t
LEFT JOIN t_sys_mpos_info t1 ON t.sn = t1.sn
LEFT JOIN t_user_Info t2 ON t.user_id = t2.id

	t1.manager_id = #{map.manager_id}

		AND t.id = #{map.id}


		AND t.user_id = #{map.user_id}


		AND t.sn = #{map.sn}


		AND t.status = #{map.status}


		AND t2.user_tel = #{map.send_user_tel}


		AND t2.real_name = #{map.real_name}



		AND t.cre_date >= date_format(#{map.beginTime},'%Y%m%d')



		AND t.cre_date <= date_format(#{map.endTime},'%Y%m%d')



${map.dataScope}

SELECT
	t.id,
	t.user_id,
	t.sn,
	t.status,
	t.remark,
	t.update_by,
	DATE_FORMAT(
		STR_TO_DATE(
			CONCAT(t.cre_date, t.cre_time),
			'%Y%m%d%H%i%s'
		),
		'%Y-%m-%d %H:%i:%s'
	) AS cre_date,
	DATE_FORMAT(
		STR_TO_DATE(
			CONCAT(t.up_date, t.up_time),
			'%Y%m%d%H%i%s'
		),
		'%Y-%m-%d %H:%i:%s'
	) AS up_date,
	t1.manager_id,
	t2.user_tel,
	t2.real_name 
FROM
	t_user_mpos_unbind_record_info t
LEFT JOIN t_sys_mpos_info t1 ON t.sn = t1.sn
LEFT JOIN t_user_Info t2 ON t.user_id = t2.id

	t1.manager_id = #{map.manager_id}

		AND t.id = #{map.id}


		AND t.user_id = #{map.user_id}


		AND t.sn = #{map.sn}


		AND t.status = #{map.status}


		AND t2.user_tel = #{map.send_user_tel}


		AND t2.real_name = #{map.real_name}



		AND t.cre_date >= date_format(#{map.beginTime},'%Y%m%d')



		AND t.cre_date <= date_format(#{map.endTime},'%Y%m%d')



${map.dataScope}

mapper.update:
update t_user_mpos_unbind_record_info 
	set status=#{map.new_status},remark=#{map.remark},
		up_date=#{map.up_date},up_time=#{map.up_time},update_by=#{map.update_by}
where id=#{map.unbind_id} and status=#{map.old_status}
