mapper.select:
SELECT t.id, t.sn, t.user_id, t.card_settle_price, t.cloud_settle_price, t.weixin_settle_price, t.zhifubao_settle_price, t.card_settle_price_vip, t.cloud_settle_price_vip, t.weixin_settle_price_vip, t.zhifubao_settle_price_vip, t.single_profit_rate, t.cash_back_rate, t.mer_cap_fee, t.state_status, t.activity_status, t.del, t.trade_status, DATE_FORMAT( STR_TO_DATE( CONCAT(t.trade_date, t.trade_time), '%Y%m%d%H%i%s' ), '%Y-%m-%d %H:%i:%s' ) AS trade_date, DATE_FORMAT( STR_TO_DATE( CONCAT(t.cre_date, t.cre_time), '%Y%m%d%H%i%s' ), '%Y-%m-%d %H:%i:%s' ) AS cre_date, DATE_FORMAT( STR_TO_DATE( CONCAT(t.up_date, t.up_time), '%Y%m%d%H%i%s' ), '%Y-%m-%d %H:%i:%s' ) AS up_date, t1.id AS pos_id, t1.mer_id, t1.mer_name, t1.name, t1.tel, t1.cash_back_type, t1.cash_back_condition, t1.cash_back_money, t1.real_cash_back_money, t1.cash_back_status, t1.credit_card_rate, t1.cloud_flash_rate, t1.weixin_rate, t1.zhifubao_rate, t1.scan_status, t1.act_status, t1.act_user_id, DATE_FORMAT( STR_TO_DATE( CONCAT(t1.act_date, t1.act_time), '%Y%m%d%H%i%s' ), '%Y-%m-%d %H:%i:%s' ) AS act_date, t2.manager_id, t2.user_tel, t2.real_name FROM t_user_traditional_pos_info t LEFT JOIN t_sys_traditional_pos_info t1 ON t.sn = t1.sn LEFT JOIN t_user_info t2 ON t.user_id = t2.id
t2.manager_id = #{map.manager_id} and t1.pos_type is null
AND t.id = #{map.id}
AND t.user_id = #{map.user_id}
AND t.sn = #{map.sn}
AND FIND_IN_SET(t.sn,#{map.sn_list})
AND t.state_status = #{map.state_status}
AND t.activity_status = #{map.activity_status}
AND t.del = #{map.del}
AND t.trade_status = #{map.trade_status}
AND t1.mer_id = #{map.mer_id}
AND t1.mer_name = #{map.mer_name}
AND t1.name = #{map.name}
AND t1.tel = #{map.tel}
AND t1.cash_back_type = #{map.cash_back_type}
AND t1.cash_back_status = #{map.cash_back_status}
AND t1.act_status = #{map.act_status}
AND t2.user_tel = #{map.user_tel}
AND t2.real_name = #{map.real_name}
AND t1.act_date >= date_format(#{map.beginTimeA},'%Y%m%d')
AND t1.act_date <= date_format(#{map.endTimeA},'%Y%m%d')
AND t.cre_date >= date_format(#{map.beginTime},'%Y%m%d')
AND t.cre_date <= date_format(#{map.endTime},'%Y%m%d')
${map.dataScope}

SELECT t.id, t.sn, t.user_id, t.card_settle_price, t.cloud_settle_price, t.weixin_settle_price, t.zhifubao_settle_price, t.single_profit_rate, t.cash_back_rate, t.mer_cap_fee, t.state_status, t.activity_status, t.del, t.trade_status, DATE_FORMAT( STR_TO_DATE( CONCAT(t.trade_date, t.trade_time), '%Y%m%d%H%i%s' ), '%Y-%m-%d %H:%i:%s' ) AS trade_date, DATE_FORMAT( STR_TO_DATE( CONCAT(t.cre_date, t.cre_time), '%Y%m%d%H%i%s' ), '%Y-%m-%d %H:%i:%s' ) AS cre_date, DATE_FORMAT( STR_TO_DATE( CONCAT(t.up_date, t.up_time), '%Y%m%d%H%i%s' ), '%Y-%m-%d %H:%i:%s' ) AS up_date, t1.id AS pos_id, t1.mer_id, t1.mer_name, t1.name, t1.tel, t1.cash_back_type, t1.cash_back_condition, t1.cash_back_money, t1.real_cash_back_money, t1.cash_back_status, t1.credit_card_rate, t1.cloud_flash_rate, t1.weixin_rate, t1.zhifubao_rate, t1.scan_status, t1.act_status, t1.act_user_id, DATE_FORMAT( STR_TO_DATE( CONCAT(t1.act_date, t1.act_time), '%Y%m%d%H%i%s' ), '%Y-%m-%d %H:%i:%s' ) AS act_date, t2.manager_id, t2.user_tel, t2.real_name FROM t_user_traditional_pos_info t LEFT JOIN t_sys_traditional_pos_info t1 ON t.sn = t1.sn LEFT JOIN t_user_info t2 ON t.user_id = t2.id
t2.manager_id = #{map.manager_id} AND t1.pos_type is null
AND t.id = #{map.id}
AND t.user_id = #{map.user_id}
AND t.sn = #{map.sn}
AND FIND_IN_SET(t.sn,#{map.sn_list})
AND t.state_status = #{map.state_status}
AND t.activity_status = #{map.activity_status}
AND t.del = #{map.del}
AND t.trade_status = #{map.trade_status}
AND t1.mer_id = #{map.mer_id}
AND t1.mer_name = #{map.mer_name}
AND t1.name = #{map.name}
AND t1.tel = #{map.tel}
AND t1.cash_back_type = #{map.cash_back_type}
AND t1.cash_back_status = #{map.cash_back_status}
AND t1.act_status = #{map.act_status}
AND t2.user_tel = #{map.user_tel}
AND t2.real_name = #{map.real_name}
AND t1.cre_date >= date_format(#{map.beginTimeA},'%Y%m%d')
AND t1.act_date <= date_format(#{map.endTimeA},'%Y%m%d')
AND t.act_date >= date_format(#{map.beginTime},'%Y%m%d')
AND t.cre_date <= date_format(#{map.endTime},'%Y%m%d')
${map.dataScope}

mapper.update:
update 
	t_user_traditional_pos_info
SET activity_status=#{map.new_activity_status},
	up_date=#{map.up_date},up_time=#{map.up_time} 
WHERE FIND_IN_SET(sn,#{map.sn_list}) AND user_id=#{map.user_id} 
	AND activity_status=#{map.old_activity_status}

update t_user_traditional_pos_info SET card_settle_price=#{card_settle_price},cloud_settle_price=#{cloud_settle_price}, weixin_settle_price=#{weixin_settle_price},zhifubao_settle_price=#{zhifubao_settle_price}, card_settle_price_vip=#{card_settle_price_vip}, single_profit_rate=#{single_profit_rate}, cash_back_rate=#{cash_back_rate},mer_cap_fee=#{mer_cap_fee}, state_status=#{state_status},del=#{del},update_by=#{create_by}, up_date=#{cre_date},up_time=#{cre_time}
,remark = #{remark}
WHERE id=#{id} and sn=#{sn} and user_id=#{user_id}

update t_user_traditional_pos_info
card_settle_price = #{map.card_settle_price}, card_settle_price_vip = #{map.card_settle_price_vip}, cloud_settle_price = #{map.cloud_settle_price}, weixin_settle_price = #{map.weixin_settle_price}, zhifubao_settle_price = #{map.zhifubao_settle_price}, single_profit_rate = #{map.single_profit_rate}, cash_back_rate = #{map.cash_back_rate}, mer_cap_fee = #{map.mer_cap_fee}, remark = #{map.remark}, state_status = #{map.state_status}, update_by = #{map.update_by}, up_date = #{map.up_date}, up_time = #{map.up_time},
where id=#{map.user_pos_id}
