mapper.select:
SELECT t1.sn, t1.credit_card_rate FROM t_user_traditional_pos_info t INNER JOIN t_sys_traditional_pos_info t1 on t.sn = t1.sn where t.user_id = #{map.sys_user_id} and t.state_status = '1'
and t.pos_type='epos'
and t.pos_type is null
AND t.sn >= #{map.start_number}
AND t.sn <= #{map.end_number}
AND NOT EXISTS ( SELECT 1 FROM t_user_apply_cardrate_trapos_record_info t2 WHERE t2.user_id = #{map.sys_user_id} AND t2.sn = t.sn AND t2.status = '00' ) order by t.id desc

SELECT t1.sn, t1.credit_card_rate FROM t_user_mpos_info t INNER JOIN t_sys_mpos_info t1 on t.sn = t1.sn where t.user_id = #{map.sys_user_id} and t.state_status = '1'
AND t.sn >= #{map.start_number}
AND t.sn <= #{map.end_number}
AND NOT EXISTS ( SELECT 1 FROM t_user_apply_cardrate_mpos_record_info t2 WHERE t2.user_id = #{map.sys_user_id} AND t2.sn = t.sn AND t2.status = '00' ) order by t.id desc

select cast(id as char) as apply_id, sn, credit_card_rate_old, credit_card_rate_new, status, remark, DATE_FORMAT(STR_TO_DATE(CONCAT(cre_date, cre_time),'%Y%m%d%H%i%s'),'%Y-%m-%d %H:%i:%s') AS cre_datetime from t_user_apply_cardrate_trapos_record_info where user_id = #{map.sys_user_id}
and pos_type='epos'
and pos_type is null
AND sn like concat('%', #{map.key_word}, '%')
AND id < #{map.last_id}
order by id desc limit 0,10

select cast(id as char) as apply_id, sn, credit_card_rate_old, credit_card_rate_new, status, remark, DATE_FORMAT(STR_TO_DATE(CONCAT(cre_date, cre_time),'%Y%m%d%H%i%s'),'%Y-%m-%d %H:%i:%s') AS cre_datetime from t_user_apply_cardrate_mpos_record_info where user_id = #{map.sys_user_id}
AND sn like concat('%', #{map.key_word}, '%')
AND id < #{map.last_id}
order by id desc limit 0,10
